        res=res.text.split('#')


        self.tableWidget.setRowCount(len(res))
        row=0
        for e in res:

            txid=e[:32]
            amount=e[e.rfind(':')+1:].strip()
            self.tableWidget.setItem(row, 0, QtWidgets.QTableWidgetItem(txid))
            self.tableWidget.setItem(row, 1, QtWidgets.QTableWidgetItem(amount))
