    def copiatxid(self):
        #txid=self.tableWidget.currentText()
        txid=self.tableWidget.currentItem().text()
        cmd='echo '+txid+'|clip'
        return subprocess.check_call(cmd, shell=True)

    def apriweb(self):
        with open('data/explorer.txt','r') as f:
            url=f.read()
        webtx=url+self.tableWidget.currentItem().text()+'.html'
        webbrowser.open(webtx)

